import Header from "./Component/Header";
import Prdouct from "./Component/PrdouctsList";
import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
function App() {
  const [items, setItems] = useState([]);
  const handleItems = (item) => {
    setItems([...items, item]);
    console.log(items);
  };
  return (
    <div>
      <BrowserRouter>
        <Header items={items} />
        <Routes>
          <Route path="/" element={<Prdouct handleItems={handleItems} />}>
            <Route path="blogs" element={<Cart />} />
          </Route>
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
