import Header from "./Component/Header";
import Prdouct from "./Component/PrdouctsList";
import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import "./Component/Cart";
function App() {
  const [items, setItems] = useState([]);
  const handleItems = (item) => {
    setItems([...items, item]);
    console.log(items);
  };
  return (
    <div>
      <BrowserRouter>
        <Header items={items} />
        <Route path="/" element={<Prdouct handleItems={handleItems} />}>
          <Route path="blogs" element={<Cart items={items} />} />
        </Route>
      </BrowserRouter>
    </div>
  );
}

export default App;
