import React from "react";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  Paper,
  Button,
} from "@material-ui/core";

const combineitems = (items) => {
  const combineitem = {};
  items.forEach((item) => {
    if (combineitem[item.id]) {
      combineitem[item.id].quantity += 1;
    } else {
      combineitem[item.id] = { ...item, quantity: 1 };
    }
  });
  const itemstotal = combineitemstotal(combineitem);
  return Object.values(itemstotal);
};

const combineitemstotal = (items) => {
  const itemtotal = {};
  items.forEach((item) => {
    itemtotal[item.id] = { ...item, total: item.price * item.quantity };
  });
  return Object.values(itemtotal);
};
const Cart = ({ items, handleRemoveFromCart }) => {
  const combinedItems = combineitems(items);
  return (
    <Paper>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Product</TableCell>
            <TableCell>Price</TableCell>
            <TableCell>Quantity</TableCell>
            <TableCell>Total</TableCell>
            <TableCell>Action</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {combinedItems.map((item) => (
            <TableRow key={item.id}>
              <TableCell>{item.title}</TableCell>
              <TableCell>${item.price}</TableCell>
              <TableCell>${item.total}</TableCell>
              <TableCell>{item.quantity}</TableCell>
              <TableCell>
                <Button onClick={() => handleRemoveFromCart(item)}>
                  Remove
                </Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </Paper>
  );
};

export default Cart;
